Trigger.script:
inches=tonumber(matches[2])*12.0+tonumber(matches[3]) local meters=inches*2.540/100.0 pounds=tonumber(matches[4])+tonumber(matches[5])/10.0 local kg=pounds*0.454 local bmi=kg/meters^2
bmi=bmi - bmi%0.001
echo(" BMI: "..bmi..".")

local wornweight=tonumber(matches[4])+tonumber(matches[5])/10.0 local mudbmi=10.0*(pounds+wornweight)/inches^2
mudbmi=mudbmi - mudbmi%0.001
echo(" MUD BMI: "..mudbmi..".")

send("change mood brave")
send("change wimpy 0")
send("change posture offensive")

char=char or {maxhealth=415,con=19,wil=12,fleelag=0}

horse=horse or {primary= "white"}
mob=mob or {primary = "caemlyn"}
weapon=weapon or {primary="sword"}
targetting=targetting or {primary=nil}
light=light or {primary="lightstick"}
container=container or {primary="pack"}
drink=drink or {primary="flask"}
door=door or {primary="oakdoor"}

targetting.charging=targetting.charging or true
targetting.backstabbing=targetting.backstabbing or false
targetting.channeling=targetting.channeling or false

char = char or {}
char.wil = tonumber(matches[4])
char.con=tonumber(matches[6])

