Alias.script:
--syntax: lh
horse = horse or {}
if horse.first then send("lead "..horse.first) else cecho("<red>Horse not set!\n") end

--syntax: ehh
horse = horse or {}
if horse.first then send("eq "..horse.first.." horseeq") else cecho("<red>Horse not set!\n") end

--syntax: dh
horse = horse or {}
if horse.first then send("diagnose " .. horse.first) else cecho("<red>Horse not set!\n") end

--syntax: delhorse
horse = horse or {}
horse.first=nil cecho("<red>Horse deleted!\n")

--syntax: oha
horse = horse or {}
if horse.first then send("order "..horse.first.." assist Adael") else cecho("<red>Horse not set!\n") end

--syntax: ohs
horse = horse or {}
if horse.first then send("order "..horse.first.." sit") else cecho("<red>Horse not set!\n") end

--syntax: ohff
horse = horse or {}
if horse.first then send("order "..horse.first.." flee") else cecho("<red>Horse not set!\n") end

--syntax: ohd
horse = horse or {}
if horse.first then send("order "..horse.first.." disengage") else cecho("<red>Horse not set!\n") end

--syntax: ohk xxx
horse = horse or {}
if horse.first then send("order "..horse.first.." kill h."..matches[2]) else cecho("<red>Horse not set!\n") end

--syntax: ohr
horse = horse or {}
if horse.first then send("order "..horse.first.." rescue Adael") else cecho("<red>Horse not set!\n") end
