Alias.script:
--syntax: glp
container = container or {} light = light or {}
if container.first then if light.first then send("get "..light.first.." "..container.first) else cecho("<red>Light not set!\n") end else cecho("<red>Container not set!\n") end

--syntax: hl
light = light or {}
if light.first then send("hold "..light.first) else cecho("<red>Light not set!\n") end

--syntax: el
light = light or {}
if light.first then send("exam "..light.first) else cecho("<red>Light not set!\n") end

--syntax: rl
light = light or {}
if light.first then send("remove "..light.first) else cecho("<red>Light not set!\n") end

--syntax: plp
container = container or {} light = light or {}
if container.first then if light.first then send("put "..light.first.." "..container.first) else cecho("<red>Light not set!\n") end else cecho("<red>Container not set!\n") end

--syntax: dellight
light = light or {}
light.first=nil cecho("<red>Light deleted!\n")
