Alias.script:
--syntax: omk OR omk xxx
mob=mob or {}
if mob.first then if matches[3] then send("order "..mob.first.." kill h."..matches[4]) elseif mob.target then send("order "..mob.first.." kill h."..mob.target) else cecho("<red>No target specified!\n") end else cecho("<red>Mob not set!\n") end

--syntax: omb OR omb xxx
mob=mob or {}
if mob.first then if matches[3] then send("order "..mob.first.." bash h."..matches[4]) else send("order "..mob.first.." bash") end else cecho("<red>Mob not set!\n") end

--syntax: oma OR oma xxx
mob=mob or {}
if mob.first then if matches[3] then send("order "..mob.first.." assist "..matches[4]) else send("order "..mob.first.." assist Adael") end else cecho("<red>Mob not set!\n") end

--syntax: omr OR omr xxx
mob=mob or {}
if mob.first then if matches[3] then send("order "..mob.first.." rescue "..matches[4]) else send("order "..mob.first.." rescue Adael") end else cecho("<red>Mob not set!\n") end

--syntax: oms
mob=mob or {}
if mob.first then send("order "..mob.first.." sit") else cecho("<red>Mob not set!\n") end

--syntax: delmob
mob=mob or {}
mob.first=nil cecho("<red>Mob deleted!\n")

--syntax: delmobtgt
mob=mob or {}
mob.target=nil cecho("<red>Mob target deleted!\n")

--syntax: omf
mob=mob or {}
if mob.first then send("order "..mob.first.." follow Adael") else cecho("<red>Mob not set!\n") end

--syntax: ombt
mob=mob or {}
