Alias.script:
statting = statting or {}
statting.stats = { str = tonumber(matches[2]), int = tonumber(matches[3]), wil = tonumber(matches[4]), dex = tonumber(matches[5]), con = tonumber(matches[6]), }
if matches[7] then statting.stats.sum = tonumber(matches[7]) else statting.stats.sum = statting.stats.str + statting.stats.int + statting.stats.wil + statting.stats.dex + statting.stats.con end
enableTrigger("Statting") statting.statting = true sendAll("score", "stat", "kill sapling")
