Alias.script:
--syntax: t1 xxx, t2 xxx, t3 xxx, t4 xxx, etc.
local colors = { dark = "ansi_red", light = "ansi_cyan", trolloc = "ansi_red", myrddraal = "ansi_red", dreadlord = "ansi_red", grayman = "ansi_red", draghkar = "ansi_red", human = "ansi_cyan", seanchan = "ansi_magenta", ogier = "ansi_green", aiel = "ansi_white" }
targeting = targeting or {}
local index = tonumber(matches[2]) local tgt = matches[3] local color = colors[tgt] or "reset"
targeting[index] = tgt cecho("Target "..index.." set to: <"..color..">"..tgt.."\n")

--syntax: show targets
local colors = { dark = "ansi_red", light = "ansi_cyan", trolloc = "ansi_red", myrddraal = "ansi_red", dreadlord = "ansi_red", grayman = "ansi_red", draghkar = "ansi_red", human = "ansi_cyan", seanchan = "ansi_magenta", ogier = "ansi_green", aiel = "ansi_white" }
targeting = targeting or {}
if targeting.currtgt then local color = colors[targeting.currtgt] or "reset" cecho("Your current target is: <"..color..">"..targeting.currtgt.."\n") end
local pkmode = (targeting.bashing and "bashing") or (targeting.charging and "charging") or (targeting.stabbing and "stabbing") or (targeting.weaving and "weaving") or "none" cecho("Your current pk mode is: "..pkmode.."\n")
for k,v in ipairs(targeting) do local color = colors[v] or "reset" cecho("Target "..k.." = <"..color..">"..v.."\n") end

--syntax: tgt xxx
local colors = { dark = "ansi_red", light = "ansi_cyan", trolloc = "ansi_red", myrddraal = "ansi_red", dreadlord = "ansi_red", grayman = "ansi_red", draghkar = "ansi_red", human = "ansi_cyan", seanchan = "ansi_magenta", ogier = "ansi_green", aiel = "ansi_white" }
targeting = targeting or {}
local tgt = matches[2] local color = colors[tgt] or "reset"
targeting.currtgt = tgt cecho("Current target set to: <"..color..">" .. tgt .. "\n")

--syntax: tgt
local colors = { dark = "ansi_red", light = "ansi_cyan", trolloc = "ansi_red", myrddraal = "ansi_red", dreadlord = "ansi_red", grayman = "ansi_red", draghkar = "ansi_red", human = "ansi_cyan", seanchan = "ansi_magenta", ogier = "ansi_green", aiel = "ansi_white" }
targeting = targeting or {}
if targeting.currtgt then local tgt = targeting.currtgt local color = colors[tgt] or "reset"
cecho("Current target set to: <"..color..">" .. tgt .. "\n") else cecho("<red>Current target not set!\n") end

--syntax: deltgt
targeting=targeting or {}
targeting.currtgt=nil cecho("<red>Current target deleted!\n")
