Alias.script:
--syntax: gyp
container = container or {}
if container.primary then send("get fluid "..container.primary) else cecho("<red>Container not set!\n") end

--syntax: grp
container = container or {}
if container.primary then send("get red "..container.primary) else cecho("<red>Container not set!\n") end

--syntax: gdp
container = container or {}
if container.primary then send("get draught "..container.primary) else cecho("<red>Container not set!\n") end

--syntax: gsp
container = container or {}
if container.primary then send("get syrup "..container.primary) else cecho("<red>Container not set!\n") end

--syntax: gtp
container = container or {}
if container.primary then send("get tea "..container.primary) else cecho("<red>Container not set!\n") end
