Script.script:
practice_trainer.style_sheets = practice_trainer.style_sheets or {}

practice_trainer.style_sheets.plus = [[
    background-color: white;
    border-width: 3px;
    border-style: solid;
    border-color: green;
    border-radius: 3px;
  ]]

practice_trainer.style_sheets.minus = [[
    background-color: white;
    border-width: 3px;
    border-style: solid;
    border-color: red;
    border-radius: 3px;
  ]]

practice_trainer.style_sheets.scrollable = [[
    background-color: white;
    border-width: 3px;
    border-style: solid;
    border-color: gray;
    border-radius: 3px;
  ]]

practice_trainer.pracs.queue = practice_trainer.pracs.queue or {}
function practice_trainer:push_queue(skill) table.insert(self.pracs.queue, skill) end
function practice_trainer:pop_queue(skill) local skill_pos = table.collect( self.pracs.queue, function(key, value) if value == skill then return true end end ) local max_pos = table.maxn(skill_pos) table.remove(self.pracs.queue, max_pos) end

practice_trainer["reset_pracs_label"] =
  practice_trainer["reset_pracs_label"] or
  Geyser.Label:new(
    {
      name = "practice_trainer.reset_pracs_label",
      x = "90%",
      y = "65%",
      width = "10%",
      height = "5%",
      fgColor = "black",
      color = "white",
      message = [[<center>Reset</center>]],
    },
    practice_trainer.container
  )
practice_trainer["reset_pracs_label"]:setStyleSheet([[
  background-color: gray;
  border-width: 3px;
  border-style: solid;
  border-color: gray;
  border-radius: 3px;
]]) 
practice_trainer["reset_pracs_label"]:setClickCallback("practice_trainer:reset_pracs")

function practice_trainer:reset_pracs()
  for _, class in pairs(self.pracs.classes) do
    self[class].pracs = {}
    self[class].percents = {}
    for _, skill in pairs(self.pracs[class]) do
      self[class].pracs[skill] = 0
      self[class].percents[skill] = 0
    end
  end
  self.pracs.queue = {}
  self:update_displays()
end

function practice_trainer:get_total_class_pracs() local prac_totals = {}
for _, class in pairs(self.pracs.classes) do prac_totals[class] = 0 for _, skill in pairs(self.pracs[class]) do prac_totals[class] = prac_totals[class] + self[class].pracs[skill] end end return prac_totals end
function practice_trainer:get_total_pracs() local prac_totals = self:get_total_class_pracs() if self.char.class == "warrior" then return prac_totals.warrior + 3*prac_totals.rogue + 2*prac_totals.hunter elseif self.char.class == "rogue" then return 3*prac_totals.warrior + prac_totals.rogue + 2*prac_totals.hunter elseif self.char.class == "hunter" then return 2*prac_totals.warrior + 2*prac_totals.rogue + prac_totals.hunter elseif self.char.class == "fade" then return prac_totals.warrior + prac_totals.rogue + prac_totals.hunter + prac_totals.fade elseif self.char.class == "channeler" then if self.char.sex == "male" then return 3*prac_totals.warrior + 3*prac_totals.rogue + 2*prac_totals.hunter else return 4*prac_totals.warrior + 3*prac_totals.rogue + 2*prac_totals.hunter end end --return error if something unknown passes through here return nil end
function practice_trainer:pracs_to_level() local prac_gain = {human = 5, seanchan = 5, trolloc = 3, fade = 4, dreadlord = 5, grayman = 5}
local level = 1 local pracs = 8
while pracs < self:get_total_pracs() do if level < 30 then pracs = pracs + prac_gain[self.char.race] else pracs = pracs + 2 end level = level + 1 end return level end
